var parseBuffer = function (encoding) {
    var encoding = encoding || '1';
    var data = '';
    switch (encoding) {
        case '1':
            data = '1';
            break;
        case '2':
            zlib.gunzip(buf, function (err, decoded) {
                data = decoded && decoded.toString();
            });
            break;
        case '3':
            zlib.inflate(buf, function (err, decoded) {
                data = decoded && decoded.toString()
            });
            break;
        default:
            data = buf.toString();
    }
    return data;
};