var zlib = require('zlib');
var request = require('request');
var querystring = require('querystring');

var rte = function (params) {
    this.port = params.port;
    this.host = params.host;

    this.options = {
        headers: {
            'Content-Type': 'application/x-www-form-urlencoded'
        },
        jar: request.jar()
    };
};

module.exports = rte;

rte.prototype.get = function (path, callback) {
    this.options.url = 'http://' + this.host + ':' + this.port + path;

    var self = this;
    var req = request.get(this.options);

    req.on('response', function (res) {
        var chunks = [];

        res.on('data', function (chunk) {
            chunks.push(chunk);
        });

        res.on('end', function () {
            self.options.Cookie = self.options.jar.getCookieString(self.options.url);

            callback();
        });
    });

    req.on('error', function (err) {
        callback(err);
    });
};

rte.prototype.request = function (path, param, callback) {
    this.options.url = 'http://' + this.host + ':' + this.port + path;
    this.options.body = querystring.stringify(param);

    var self = this;
    var req = request.post(this.options);

    req.on('response', function (res) {
        var chunks = [];

        res.on('data', function (chunk) {
            chunks.push(chunk);
        });

        res.on('end', function () {
            self.options.Cookie = self.options.jar.getCookieString(self.options.url);

            var buffer = Buffer.concat(chunks);
            var encoding = res.headers['content-encoding'];
            if (encoding == 'gzip') {
                zlib.gunzip(buffer, function (err, decoded) {
                    callback(decoded && decoded.toString());
                });
            } else if (encoding == 'deflate') {
                zlib.inflate(buffer, function (err, decoded) {
                    callback(decoded && decoded.toString());
                })
            } else {
                callback(buffer.toString());
            }
        });
    });

    req.on('error', function (err) {
        callback(err);
    });
};